<?php
class xu_ly
{
    public $dong;
    public $cot;
    public $rong;
    public $day;
    public $khoangCachO;
    public $khoangCachDong;
    public $noiDung;
    public $noiDungDong;
    public $noiDungCot;
    public $mau;
    public function setdong($dong)
    {
        $this->dong = $dong;
    }
    public function getdong()
    {
        return $this->dong;
    }
    public function setcot($cot)
    {
        $this->cot = $cot;
    }
    public function getcot()
    {
        return $this->cot;
    }
    public function setrong($rong)
    {
        $this->rong = $rong;
    }
    public function getrong()
    {
        return $this->rong;
    }
    public function setday($day)
    {
        $this->day = $day;
    }
    public function getday()
    {
        return $this->day;
    }
    public function setkhoangCachO($khoangCachO)
    {
        $this->khoangCachO = $khoangCachO;
    }
    public function getkhoangCachO()
    {
        return $this->khoangCachO;
    }
    public function setkhoangCachDong($khoangCachDong)
    {
        $this->khoangCachDong = $khoangCachDong;
    }
    public function getkhoangCachDong()
    {
        return $this->khoangCachDong;
    }
