    {
        $this->noiDung = $noiDung;
    }
    public function getnoiDung()
    {
        return $this->noiDung;
    }
    public function setnoiDungDong($noiDungDong)
    {
        $this->noiDungDong = $noiDungDong;
    }
    public function getnoiDungDong()
    {
        return $this->noiDungDong;
    }
    public function setnoiDungCot($noiDungCot)
    {
        $this->noiDungCot = $noiDungCot;
    }
    public function getnoiDungCot()
    {
        return $this->noiDungCot;
    }
    public function setmau($mau)
    {
        $this->mau = $mau;
    }
    public function getmau()
    {
        return $this->mau;
    }
}
